let selectedDate;
if (msg.topic === 'date') {
    if (msg.payload === undefined || msg.payload === null || msg.payload === '0' || msg.payload === 0) {
        selectedDate = flow.get('selectedDate') || new Date().toISOString().split('T')[0];
        node.log(`Using default date: ${selectedDate}`);
    } else if (typeof msg.payload === 'number' && msg.payload > 0) {
        selectedDate = new Date(msg.payload).toISOString().split('T')[0];
    } else if (typeof msg.payload === 'string' && msg.payload.match(/^\d{4}-\d{2}-\d{2}T/)) {
        selectedDate = msg.payload.split('T')[0];
    } else if (typeof msg.payload === 'string' && msg.payload.match(/^\d{4}-\d{2}-\d{2}$/)) {
        selectedDate = msg.payload;
    } else {
        node.warn(`Invalid date selected: ${msg.payload}`);
        return null;
    }
    if (selectedDate.match(/^\d{4}-\d{2}-\d{2}$/)) {
        flow.set('selectedDate', selectedDate);
        node.log(`Selected date: ${selectedDate}`);
    } else {
        node.warn(`Invalid date format after conversion: ${selectedDate}`);
        return null;
    }
} else if (msg.topic === 'refresh') {
    selectedDate = flow.get('selectedDate') || new Date().toISOString().split('T')[0];
    node.log(`Using stored or default date for refresh: ${selectedDate}`);
}

msg.url = `http://localhost:3000/data?date=${selectedDate}`;
msg.method = 'GET';
node.log(`Requesting data for date: ${selectedDate}`);
return msg;

const data = msg.payload;

if (!Array.isArray(data)) {
    node.warn('Invalid or empty data from API: ' + JSON.stringify(data));
    const emptyMsg = {
        payload: [{ series: ['temperature_c'], data: [[]], labels: ['Temperature (Â°C)'] }],
        error: data.error || 'No data found for selected date'
    };
    const emptyMsg2 = {
        payload: [{ series: ['humidity_pct'], data: [[]], labels: ['Humidity (%)'] }],
        error: data.error || 'No data found for selected date'
    };
    return [emptyMsg, emptyMsg2, emptyMsg];
}

// Prepare Temperature Chart
const tempData = data.map(row => ({
    x: new Date(row.timestamp).getTime(),
    y: parseFloat(row.temperature_c) || 0
}));

// Prepare Humidity Chart
const humiData = data.map(row => ({
    x: new Date(row.timestamp).getTime(),
    y: parseFloat(row.humidity_pct) || 0
}));

// Output 1: Temperature data
const tempMsg = {
    payload: [{ series: ['temperature_c'], data: [tempData], labels: ['Temperature (Â°C)'] }],
    error: null
};

// Output 2: Humidity data
const humiMsg = {
    payload: [{ series: ['humidity_pct'], data: [humiData], labels: ['Humidity (%)'] }],
    error: null
};

node.log(`Prepared chart data: ${data.length} rows`);
return [tempMsg, humiMsg, null];

if (msg.topic === 'config') {
    const { device_id, TEMP_MAX, TEMP_MIN, HUMI_MIN, HUMI_MAX } = msg.payload;
    const pi_name = device_id || flow.get('pi_name') || 'nhom2'; // Default to 'nhom2' if device_id and flow.pi_name are empty
    flow.set('pi_name', pi_name); // Store pi_name in flow context
    node.log(`Stored pi_name: ${pi_name}`);

    const config = {};
    if (TEMP_MAX !== undefined && TEMP_MAX !== '') config.TEMP_MAX = parseFloat(TEMP_MAX);
    if (TEMP_MIN !== undefined && TEMP_MIN !== '') config.TEMP_MIN = parseFloat(TEMP_MIN);
    if (HUMI_MIN !== undefined && HUMI_MIN !== '') config.HUMI_MIN = parseFloat(HUMI_MIN);
    if (HUMI_MAX !== undefined && HUMI_MAX !== '') config.HUMI_MAX = parseFloat(HUMI_MAX);

    if (Object.keys(config).length === 0) {
        node.warn('At least one config value must be provided');
        msg.error = 'At least one config value must be provided';
        const formMsg = {
            payload: {
                device_id: pi_name,
                TEMP_MAX: flow.get('config.TEMP_MAX') || '',
                TEMP_MIN: flow.get('config.TEMP_MIN') || '',
                HUMI_MIN: flow.get('config.HUMI_MIN') || '',
                HUMI_MAX: flow.get('config.HUMI_MAX') || ''
            },
            topic: 'config'
        };
        return [null, formMsg]; // Still return formMsg to update UI
    }

    for (const key in config) {
        if (isNaN(config[key]) || config[key] < 0) {
            node.warn(`Invalid ${key}: ${config[key]}. Must be a non-negative number`);
            msg.error = `Invalid ${key}: Must be a non-negative number`;
            const formMsg = {
                payload: {
                    device_id: pi_name,
                    TEMP_MAX: flow.get('config.TEMP_MAX') || '',
                    TEMP_MIN: flow.get('config.TEMP_MIN') || '',
                    HUMI_MIN: flow.get('config.HUMI_MIN') || '',
                    HUMI_MAX: flow.get('config.HUMI_MAX') || ''
                },
                topic: 'config'
            };
            return [null, formMsg];
        }
    }

    if ((config.TEMP_MAX !== undefined && config.TEMP_MIN !== undefined) && config.TEMP_MAX <= config.TEMP_MIN) {
        node.warn('TEMP_MAX must be greater than TEMP_MIN');
        msg.error = 'TEMP_MAX must be greater than TEMP_MIN';
        const formMsg = {
            payload: {
                device_id: pi_name,
                TEMP_MAX: flow.get('config.TEMP_MAX') || '',
                TEMP_MIN: flow.get('config.TEMP_MIN') || '',
                HUMI_MIN: flow.get('config.HUMI_MIN') || '',
                HUMI_MAX: flow.get('config.HUMI_MAX') || ''
            },
            topic: 'config'
        };
        return [null, formMsg];
    }

    if ((config.HUMI_MAX !== undefined && config.HUMI_MIN !== undefined) && config.HUMI_MAX <= config.HUMI_MIN) {
        node.warn('HUMI_MAX must be greater than HUMI_MIN');
        msg.error = 'HUMI_MAX must be greater than HUMI_MIN';
        const formMsg = {
            payload: {
                device_id: pi_name,
                TEMP_MAX: flow.get('config.TEMP_MAX') || '',
                TEMP_MIN: flow.get('config.TEMP_MIN') || '',
                HUMI_MIN: flow.get('config.HUMI_MIN') || '',
                HUMI_MAX: flow.get('config.HUMI_MAX') || ''
            },
            topic: 'config'
        };
        return [null, formMsg];
    }

    flow.set('config', config);
    node.log(`Stored config: ${JSON.stringify(config)}`);
    const formMsg = {
        payload: {
            device_id: pi_name,
            TEMP_MAX: config.TEMP_MAX || flow.get('config.TEMP_MAX') || '',
            TEMP_MIN: config.TEMP_MIN || flow.get('config.TEMP_MIN') || '',
            HUMI_MIN: config.HUMI_MIN || flow.get('config.HUMI_MIN') || '',
            HUMI_MAX: config.HUMI_MAX || flow.get('config.HUMI_MAX') || ''
        },
        topic: 'config'
    };
    msg.topic = `warehouse/config/${pi_name}`;
    msg.payload = config;
    node.log(`Sending config to MQTT: ${JSON.stringify(config)} for device ${pi_name}`);
    return [msg, formMsg];
}
return [null, null];

